import requests, json, pandas as pd; from bs4 import BeautifulSoup; soup = [dict(zip(['No', 'Name', 'Type 1', 'Type 2', 'Total', 'HP', 'Attack', 'Defense', 'SP. Attack', 'SP. Defense', 'Speed'], i)) for i in [i[:2]+(i[2].split() if ' ' in i[2] else [i[2], ''])+i[3:] for i in [[int(j.text) if j.text.isdigit() else j.text.strip() for j in i.findAll('td')] for i in BeautifulSoup(requests.get('https://pokemondb.net/pokedex/all').content, 'lxml').find('table', {'id':'pokedex'}).find('tbody').findAll('tr')]]]; pd.DataFrame(soup).to_csv('pokedex.csv', index=False); json.dump({i['Name']:dict([list(i.items())[0]]+list(i.items())[2:]) for i in soup}, open('pokedex.json', 'w'), indent=4)